-- ----------------------------
-- Table structure for tintuc
-- ----------------------------
DROP TABLE IF EXISTS `tintuc`;
CREATE TABLE `tintuc`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `nhanVien_id` int NULL DEFAULT NULL,
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `tieuDe` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `noiDung` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL,
  `ngayTao` datetime NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `nhanVien_id`(`nhanVien_id` ASC) USING BTREE,
  CONSTRAINT `tintuc_ibfk_1` FOREIGN KEY (`nhanVien_id`) REFERENCES `nhanvien` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 8 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of tintuc
-- ----------------------------
INSERT INTO `tintuc` VALUES (1, 2, 'tintucsp_1711728809519_1.jpg', '5 BEST FRUITS TO MAKE YOU FRESH AND HEALTHY', '<p>Bên trong hộp Tecno Spark 20 Pro Plus có gì?</p><p>Trước khi chúng ta trên tay Teco Spark 20 Pro Plus thì các bạn hãy cùng mình mở hộp em nó trước nhé, Tecno vẫn giữ cách đóng hộp quen thuộc với tông trắng nửa trên và màu da cam ở bên dưới hộp ở phía trước hộp chúng ta có tên máy khá là đơn giản.</p><p>Mở hộp Tecno Spark 20 Pro Plus</p><p>Tecno vô cùng chu đáo với người dùng khi trang bị đầy đủ phụ kiện cần thiết ở bên trong máy. Cơ bản nhất là que chọc SIM sau đó là sách hướng dẫn sử dụng.</p><p><br></p><p>Mở hộp Tecno Spark 20 Pro Plus</p><p>Tiếp đến là củ và cáp sạc theo máy, tai nghe với jack cắm 3.5mm và ốp lưng trong suốt để bảo vệ thiết bị.</p><p>Củ và cáp sạc của Tecno Spark 20 Pro Plus</p><p>Không những thế, hãng còn tặng thêm cho người dùng sticker Mobile Legend để trang trí máy hoặc dán ở đâu đó và bộ cường lực màn hình cong với khăn lau và miếng hút bụi. Có thể nói với mức giá hơn 5 triệu một chút, chúng ta có được đầy đủ phụ kiện đi kèm phải nói, Tecno rất là chu đáo với người dùng.</p><p>Khi mua Tecno Spark 20 Pro Plus được tặng kèm theo cả cường lực</p><p>Khi mua Tecno Spark 20 Pro Plus được tặng kèm theo cả cường lực</p><p>Thiết kế và ngoại hình của Tecno Spark 20 Pro Plus</p><p>Thiết kế chính là điểm cộng đầu tiên và lớn nhất của Tecno Spark 20 Pro Plus, em nó sẽ dành cho những bạn đang tìm kiếm cho mình một thiết bị có ngoại hình độc đáo, sang trọng và đặc biệt là yêu thích màn hình cong trong phân khúc tầm trung.</p><p>Thiết kế và ngoại hình của Tecno Spark 20 Pro Plus</p><p>Khi cầm trên tay Tecno Spark 20 Pro Plus, mình không nghĩ đây là một thiết bị giá 5 triệu mà sở hữu một ngoại hình như những thiết bị cao cấp và cận cao cấp đúng không? Viền máy bóng bẩy, mỏng nhẹ, cụm camera to trông rất là chuyên nghiệp và màn hình cong ấn tượng.</p><p>Có thể nói trông thiết bị khác với những chiếc smartphone đến từ nhà Tecno gần đây, mỏng hơn, nhẹ hơn, sang trọng hơn và trông đầy cảm hứng hơn.</p><p>Trên tay Tecno Spark 20 Pro Plus&nbsp;</p><p>Tecno Spark 20 Pro Plus ra mắt với hai màu trắng và đen, mình thấy thích màu trắng hơn trông em nó thanh lịch và thu hút hơn hẳn, chỗ cụm camera thiết bị sẽ có thêm một dải vân cẩm thạch trông rất là độc đáo, màu đen thì cũng đẹp, thanh lịch nhưng cá nhân mình thấy hơi chìm, màu này sẽ hợp hơn với những bạn yêu thích sự tối giản, bí ẩn một chút.</p><p>Thiết kế và ngoại hình của Tecno Spark 20 Pro Plus</p><p>Tecno Spark 20 Pro Plus được hoàn thiện hoàn toàn bằng nhựa nên trọng lượng của thiết bị khá là nhẹ nhàng chỉ 179g, kích thước tổng thể 164.7 x 75 x 7.6 mm cực kỳ mỏng nhẹ tuy màn hình hơi to nhưng cảm giác cầm nắm thoải mái không quá nặng tay.</p><p>Tecno Spark 20 Pro Plus có ngoại hình mỏng nhẹ</p><p>Các cạnh của máy được mạ bóng để làm Tecno Spark 20 Pro Plus trông đẹp mắt hơn, có thể thấy bên trên là nơi đặt loa ngoài thứ nhất và mic thu âm. Em nó còn được trang bị chuẩn kháng nước, bụi IP53 gia tăng độ bền của người sử dụng.</p><p>Các cạnh bên của Tecno Spark 20 Pro Plus</p><p>Cạnh dưới là nơi đặt loa ngoài thứ 2 đạt chuẩn Hi RES Audio với mức âm lượng lên đến 400% nhờ đó mà người dùng có trải nghiệm nghe nhạc, xem phim giải trí hấp dẫn hơn, chúng ta sẽ có thêm cổng sạc USB - C, mic thoại và khe SIM.</p><p><br></p><p>Cạnh trái của Tecno Spark 20 Pro Plus được để trống, cạnh phải đặt nút nguồn và tăng/giảm âm lượng.</p><p><br></p><p>Quay đến mặt lưng của máy, do được làm dạng bóng nên cầm nắm khá là trơn, cụm camera của thiết bị được làm hình tròn thay vì hình vuông như những chiếc máy Tecno ra mắt trước đó. Phải nói nhìn từ mặt sau trông em nó rất là nổi bật, làm mình nhớ đến OnePlus 11.</p><p>Cụm camera của Tecno Spark 20 Pro Plus</p><p>Camera</p><p>Về thông số camera thì Tecno Spark 20 Pro Plus được trang bị camera chính độ phân giải 108MP, Macro 2MP đi kèm với đó là những tính năng phụ trợ như siêu ban đêm, chụp chân dung... Ngoài ra thiết bị còn hỗ trợ thêm tính năng quay phim 2K.</p><p><br></p><p>Một số hình ảnh chụp nhanh từ thiết bị:</p><p>Ảnh chụp từ Tecno Spark 20 Pro Plus</p><p><br></p><p><br></p><p><br></p><p>Ảnh chụp từ Tecno Spark 20 Pro Plus</p><p>Màn hình&nbsp;</p><p>Tecno Spark 20 Pro Plus được trang bị màn hình cong kích thước lớn 6.78 inch với camera selfie dạng đục lỗ 32MP. Có thể nói Trong tầm giá 5 triệu hơn hiếm có thiết bị nào được trang bị màn hình cong như thế, đa phần sẽ dùng màn hình phẳng thường thấy. Dĩ nhiên, viền trên và viền dưới của màn hình vẫn còn trông khá là dày.</p><p>Màn hình cong trên Tecno Spark 20 Pro Plus</p><p><br></p><p><br></p><p>Không chỉ là cụm camera nốt ruồi bình thường, mà \"nốt ruồi\" này còn có thể biến thành \"Dynamic isLand\". Nói vậy thôi chứ tính năng này trên Tecno Spark 20 Pro gọi là màn hình Dynamic Port khá là thú vị.</p><p>Dynamic port trên Tecno Spark 20 Pro plus</p><p>Về khả năng hiển thị thì Tecno Spark 20 Pro Plus được trang bị màn hình AMOLED, độ phân giải FullHD+ (1,080 x 2,436 pixels pixels), tần số quét 120Hz, độ sáng 1000 nits. Bao nhiêu thông số đó cũng đủ cho thấy thiết bị mang đến trải nghiệm giải trí, xem phim, chơi game với màu sắc hấp dẫn đẹp mắt, trải nghiệm vuốt chạm mượt mà.</p><p>Trải nghiệm xem phim trên Tecno Spark 20 Pro Plus</p><p>Hiệu năng, phần mềm và pin</p><p>Về phần cấu hình thì thiết bị được trang bị con chip Helio G99 Ultimate - Phiên bản cải tiến hơn của Helio G99, nhân đồ hoạ Mali-G57 MC2, chạy trên nền tảng Android 14, giao diện HiOS 13, RAM 8GB, bộ nhớ trong 256GB.</p><p><br></p><p>Theo kiểm tra hiệu năng từ ứng dụng Antutu Benchmark V10 thì em nó đạt 402,771 điểm, Geekbench 6 là 563 điểm đơn nhân, 1,722 điểm đa nhân.</p><p><br></p><p>Tecno Spark 20 Pro Plus</p><p>Mình có trải nghiệm nhanh hiệu năng của em nó thì thiết bị chơi được Tốc Chiến, PUBG: Mobile với mức đồ hoạ trung bình cao cũng khá là tốt.</p><p>Trải nghiệm chơi game trên Tecno Spark 20 Pro Plus</p><p>Máy còn được tích hợp cảm biến vân tay bên trong màn hình, một thứ hiếm hoi trong phân khúc 5 triệu đồng. Về dung lượng pin thì thiết bị được trang bị viên pin dung lượng 5,000 mAh vừa đủ để người dùng sử dụng trong một ngày, đi kèm với đó là sạc nhanh công suất 33W giúp tiết kiệm thời gian sạc pin của người dùng.</p><p>Tecno Spark 20 Pro PlusCó nên mua Tecno Spark 20 Pro Plus?</p><p>Các bạn thấy sao về chiếc smartphone nhà Tecno này? Mình thấy em nó sẽ là chiếc smartphone nổi bật trong phân khúc tầm trung khi đây là thiết bị sở hữu một thiết kế đẹp mắt, sang trọng với màn hình cong ấn tượng, mỏng nhẹ vô cùng thời trang, cấu hình tốt, đủ để chơi nhiều tựa game trên thị trường, camera độ phân giải cao.</p><p>Tecno Spark 20 Pro Plus có mức giá niêm yết tại thị trường Việt Nam là 5.49 triệu. Tuy nhiên, sản phẩm có mức giá cực kỳ ưu đãi tại CellphoneS. Cụ thể, em nó đang có giá khuyến mãi là 4,890,000 đồng (giảm trực tiếp 600,000 đồng). Ngoài ra, người dùng còn nhận thêm ưu đãi Tặng Pin dự phòng BASEUS AIRPOW FAST CHARGE 20000MAh cho 100 suất đặt hàng đầu tiên và có thể quy đổi thành 300,000 đồng. Như vậy, mức giá trừ hết ưu đãi cho 100 khách hàng đầu tiên chỉ là 4.59 triệu đồng. Có thể nói đây là một mức giá cực kỳ hời dành cho một chiếc máy có chất lượng cao như này.</p>', '2024-03-30 21:22:13');
